public class PickupTransactionAddAAC extends Action {
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
    try{
      response.setContentType("text/html;charset=UTF-8");
      response.setHeader("Cache-Control", "no-cache");
      String empId = request.getParameter("empId");
      Integer orgId = (Integer)request.getSession().getAttribute("orgId");
      IPickupBS pbs = (IPickupBS)BSUtils.getBusinessService("pickupBS",this,mapping.getModuleConfig());
      boolean flag = pbs.getOrgPayMonthEmpPayMonth(orgId.toString(), empId);
      String text="displays('"+empId+"','"+orgId+"','"+flag+"')";
      response.getWriter().write(text);
      response.getWriter().close();
      return null;
    }catch(Exception s){
      s.printStackTrace();
    }
    return null;
	}
}