public class TranTaImportAC extends Action {
  /*
   * Generated Methods
   */

  /**
   * Method execute
   * 
   * @param mapping
   * @param form
   * @param request
   * @param response
   * @return ActionForward
   */
  public ActionForward execute(ActionMapping mapping, ActionForm form,
      HttpServletRequest request, HttpServletResponse response)throws Exception  {
    // TODO Auto-generated method stub
    ActionMessages messages =null;
    TranImportTaAF forms = (TranImportTaAF) form;
    

    String orgOutid = request.getParameter("orgOutid");
    orgOutid=BusiTools.convertSixNumber(orgOutid);
    String orgOutName = request.getParameter("orgOutName");

    String orgInId = request.getParameter("orgInid");
    String orgInName = request.getParameter("orgInName");

    String noteNum = request.getParameter("noteNum");
    SecurityInfo securityInfo=(SecurityInfo)request.getSession().getAttribute("SecurityInfo");
    String ip=securityInfo.getUserInfo().getUserIp();
    String name=securityInfo.getUserInfo().getUsername();
    String date=securityInfo.getUserInfo().getBizDate();
    FormFile file = forms.getTheFile();
 
    String fileType = file.getFileName().substring(
        file.getFileName().lastIndexOf(".") + 1);
    if (file.toString().length() < 1) {
      return (mapping.findForward("文件无后缀"));
    } else if (!fileType.equals("xls")) {
      return (mapping.findForward("文件后缀不是xls格式"));
    }
    ServletContext context = request.getSession().getServletContext();
    String jasperSrc = context.getRealPath("/impXml/" + "tranOut_imp.xml");
    File files = new File(forms.getUrl());
    Factory faxtory = new Factory();
    File xmlfile = new File(jasperSrc);
    Map info = null;
    try {
      InputStream stream = file.getInputStream();
      info = faxtory.getInfomation(xmlfile, stream,"org.xpup.hafmis.syscollection.tranmng.tranout.dto.");
    } catch (Exception e) {
      e.printStackTrace();
      messages=new ActionMessages();
      messages.add(ActionMessages.GLOBAL_MESSAGE, new ActionMessage("导入格式不对！" ,
          false));
      saveErrors(request, messages);
      return mapping.findForward("to_tran_showAC");
    }
    List addtranoutHeadImportList = (List) info
        .get("TranoutHeadImport");
    List addtranoutListImportList = (List) info
        .get("TranoutListImport");
    ItranoutBS tranoutBS = (ItranoutBS)
    BSUtils.getBusinessService("tranoutBS", this, mapping.getModuleConfig());

    List failList = null;
    try {
      failList = tranoutBS.addTranoutListBatch(addtranoutHeadImportList,addtranoutListImportList,
          orgOutid,orgOutName,orgInId,orgInName,noteNum,ip,name,date,securityInfo);
      if(failList!=null){
        if (failList.size() > 0) {    
          request.getSession().setAttribute("explist", failList);
          response.sendRedirect(request.getContextPath()
              + "/ExportServlet?ISCANSHU=false&EXP_NAME=tranOut_exp");
          return null;
        }
      }
    } catch (BusinessException b) {
      b.printStackTrace();
      messages=new ActionMessages();
      messages.add(ActionMessages.GLOBAL_MESSAGE, new ActionMessage("导入失败！"+b.getMessage(),
          false));
      saveErrors(request, messages);
      return mapping.findForward("tranout_imports");
    } catch (Exception e) {
      e.printStackTrace();
    }
    return mapping.findForward("to_tran_showAC");
  }

}