package org.xpup.hafmis.sysfinance.bookmng.createbook.action;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.apache.struts.action.ActionMessage;
import org.apache.struts.action.ActionMessages;
import org.xpup.common.util.BSUtils;
import org.xpup.hafmis.orgstrct.dto.SecurityInfo;
import org.xpup.hafmis.sysfinance.bookmng.createbook.bsinterface.ICreateBookBS;
import org.xpup.hafmis.sysfinance.bookmng.createbook.dto.CreateBookDTO;
import org.xpup.hafmis.sysfinance.bookmng.createbook.form.CreateBookAF;

/**
 * MyEclipse Struts Creation date: 10-13-2007 XDoclet definition:
 * 
 * @struts.action path="/createBookNewAC" name="createBookAF" scope="request"
 *                validate="true"
 */
public class CreateBookNewAC extends Action {
  /*
   * Generated Methods
   */

  /**
   * Method execute
   * 
   * @param mapping
   * @param form
   * @param request
   * @param response
   * @return ActionForward 张列
   */
  public ActionForward execute(ActionMapping mapping, ActionForm form,
      HttpServletRequest request, HttpServletResponse response) {
    //解决重复提交
    if (!isTokenValid(request, true)) {
      ActionMessages messages = new ActionMessages();
      messages.add(ActionMessages.GLOBAL_MESSAGE, new ActionMessage("请不要重复提交！",
          false));
      saveErrors(request, messages);
      saveToken(request);
    }else{
      try {
        //权限
        SecurityInfo securityInfo = (SecurityInfo) request.getSession()
            .getAttribute("SecurityInfo");
        //年 月
        String userYearMonth = request.getParameter("bookYear")
            + request.getParameter("bookMonth");
        //必须是1月1日
        //userYearMonth = request.getParameter("bookYear")+"01";
        
        CreateBookAF createBookAF = (CreateBookAF) form;
        CreateBookDTO createBookDTO = new CreateBookDTO();
        createBookDTO.setBookName(createBookAF.getBookName().trim());
        createBookDTO.setUserYearMonth(userYearMonth);
        createBookDTO.setOpIp(securityInfo.getUserIp());
        createBookDTO.setOperator(securityInfo.getUserName().trim());
        createBookDTO.setParamValue(createBookAF.getParamValue().trim());
        createBookDTO.setParamExplain1(createBookAF.getParamExplain1().trim());
        createBookDTO.setParamExplain2(createBookAF.getParamExplain2().trim());
        createBookDTO.setParamExplain3(createBookAF.getParamExplain3().trim());
        createBookDTO.setParamExplain4(createBookAF.getParamExplain4().trim());
        createBookDTO.setParamExplain5(createBookAF.getParamExplain5().trim());
        createBookDTO.setParamExplain6(createBookAF.getParamExplain6().trim());
        
        //判断统一核算或者独立核算
        int settleType = securityInfo.getFnSettleType();
        if(settleType == 0){
          //统一
          createBookDTO.setOffice("0");
        }else{
          //独立
          createBookDTO.setOffice("1");
        }
        
        ICreateBookBS createBookBS = (ICreateBookBS) BSUtils.getBusinessService(
            "createBookBS", this, mapping.getModuleConfig());
        //插入信息
        createBookBS.createBook(createBookDTO);
        
      } catch (Exception e) {
        // TODO Auto-generated catch block
        e.printStackTrace();
      }
    }
    return mapping.findForward("createBookForwoardAC");
  }
}