public class SummaryShowAC extends Action {
	/*
	 * Generated Methods
	 */
  
  public static final String PAGINATION_KEY = "org.xpup.hafmis.sysfinance.bookmng.summary.action.SummaryShowAC";

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		// TODO Auto-generated method stub
    try {
      Pagination pagination = getPagination(PAGINATION_KEY, request);
      SummaryNewAF summaryNewAF = new SummaryNewAF();
      List list = new ArrayList();
      PaginationUtils.updatePagination(pagination, request);
      //权限
      SecurityInfo securityInfo = (SecurityInfo) request.getSession()
          .getAttribute("SecurityInfo");
      //bookId
      String bookId = securityInfo.getBookId();
      ISummaryBS summaryBS = (ISummaryBS) BSUtils.getBusinessService(
          "summaryBS", this, mapping.getModuleConfig());
      list = summaryBS.findSummaryList(pagination, bookId);
      int count = summaryBS.querySummaryCount(bookId);
      if(list.size() == 0){
        request.setAttribute("noData", "0");
      }
      pagination.setNrOfElements(count);
      //修改
      if(request.getAttribute("updateInfo") != null){
        String paraId = (String)request.getAttribute("paraId");
        String paramExplain = (String)request.getAttribute("paramExplain");
        summaryNewAF.setParaId(paraId);
        summaryNewAF.setParamExplain(paramExplain);
        request.setAttribute("updateInfo", "updateInfo");
      }
      summaryNewAF.setList(list);
      request.setAttribute("summaryNewAF", summaryNewAF);
    } catch (Exception e) {
      e.printStackTrace();
    }
    return mapping.findForward("to_summary_show");
	}
  
   
  private Pagination getPagination(String paginationKey,
      HttpServletRequest request) {
    Pagination pagination = (Pagination) request.getSession().getAttribute(
        paginationKey);
    if (pagination == null) {
      pagination = new Pagination(0, 10, 1, "f102.para_id", "DESC", new HashMap(0));
      request.getSession().setAttribute(paginationKey, pagination);
    }
    return pagination;
  }
}