public class NotarialShowAC extends Action {
  public static final String PAGINATION_KEY =
    "org.xpup.hafmis.sysloan.dataready.assistantorg.action.NotarialForwardAC";

  public ActionForward execute(ActionMapping mapping, ActionForm form,
      HttpServletRequest request, HttpServletResponse response) {
    EvaluateShowListAF evaluateShowListAF=new EvaluateShowListAF();
    try{
      Pagination pagination = getPagination(PAGINATION_KEY, request);
      PaginationUtils.updatePagination(pagination, request);
      INotarialBS notarialBS=(INotarialBS)BSUtils
      .getBusinessService("notarialBS", this, mapping.getModuleConfig());
      evaluateShowListAF.setList(notarialBS.findNotarialList(pagination));
    }
    catch(Exception e)
    {
      e.printStackTrace();
    }
    request.setAttribute("ListAF", evaluateShowListAF);
    return mapping.findForward("to_notarial_show");
  }
  private Pagination getPagination(String paginationKey,    //需要改动 字符 排序
      HttpServletRequest request) {
    Pagination pagination = (Pagination) request.getSession().getAttribute(
        paginationKey);
    if (pagination == null) {   
      pagination = new Pagination(0, 10, 1, "assistantOrg.assistantOrgId ", "DESC",
          new HashMap(0));
      request.getSession().setAttribute(PAGINATION_KEY, pagination);
    }

    return pagination;
  }
}