package org.xpup.hafmis.sysloan.loanapply.specialapply.action;

import java.util.HashMap;
import java.util.Map;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.apache.struts.action.ActionMessage;
import org.apache.struts.action.ActionMessages;
import org.xpup.common.exception.BusinessException;
import org.xpup.common.util.BSUtils;
import org.xpup.hafmis.orgstrct.dto.SecurityInfo;
import org.xpup.hafmis.sysloan.loanapply.specialapply.bsinterface.ISpecialapplyBS;

/**
 * MyEclipse Struts Creation date: 09-29-2007 XDoclet definition:
 * 
 * @struts.action parameter="method" validate="true"
 */
public class SpecialapplyTbDeleteAC extends Action {
  /**
   * 单选删除 Method execute
   * @param mapping
   * @param form
   * @param request
   * @param response
   * @return ActionForward
   */
  public ActionForward execute(ActionMapping mapping, ActionForm form,
      HttpServletRequest request, HttpServletResponse response) {
    ActionMessages messages = null;
    try {
      String borrowerName = request.getParameter("borrowerName");
      String cardNum = request.getParameter("cardNum");
      ISpecialapplyBS specialapplyBS = (ISpecialapplyBS) BSUtils.
        getBusinessService("specialapplyBS", this,mapping.getModuleConfig());
      //取得权限对象
      SecurityInfo securityInfo = (SecurityInfo) request.getSession()
      .getAttribute("SecurityInfo");
      //删除 特殊借款人信息
      specialapplyBS.deleteSpecialapply(borrowerName,cardNum,securityInfo);
      messages = new ActionMessages();
      messages.add(ActionMessages.GLOBAL_MESSAGE, new ActionMessage("删除成功！",
          false));
      saveErrors(request, messages);
    } catch (BusinessException bex) {
      messages = new ActionMessages();
      messages.add(ActionMessages.GLOBAL_MESSAGE, new ActionMessage(bex
            .getLocalizedMessage().toString()));
      saveErrors(request, messages);
      bex.printStackTrace();
    }
    return mapping.findForward("specialapplyTbShowAC");
  }
}